import React from 'react'
import axios from 'axios';
import { useEffect,useState } from 'react';
import {Bar,Line,Pie} from "react-chartjs-2";
import{Chart as ChartJS} from "chart.js/auto"
import { parse } from 'papaparse';
import 'chartjs-adapter-luxon';





const urlv1gm = 'http://localhost:8080/v1gm'






export default function V1() {

  const [data1, setdata] = useState([])

  useEffect(() => {
    axios.get(urlv1gm)
      .then((response) => {
        console.log(response.data)
        setdata(response.data)




      }).catch(error => {
        alert(error.response.error)
      })

  }, [])

  const chartData = {
    labels: data1.map(d => d.time),
    datasets: [
      {
        label: 'Time',
        backgroundColor: 'rgba(75,192,0,1)',
        borderColor: 'rgba(0,0,0,255)',
        borderWidth: 1,
        data: data1,
        spanGaps: false,
        parsing: {
          xAxisKey: 'time',
          yAxisKey: 'anomalydeg'
        }
      }


    ]
  }
  const options = {
    type:'line',
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Global historical surface temperature anomalies from January 1850 onwards monthly",
      },

    },
    scales: {
      y: { min: -3, max: 3 },
      x: { type: 'time' }

    },
  }








  return (
    <div>
      <Line options={options} data={chartData} />
    </div>
  )
}
